const raw = $input.first().json;
const body = raw.body || raw;

const startTime = Date.now();

const angles = body.angles || ['front'];
if (!Array.isArray(angles) || angles.length === 0) {
  throw new Error('Nenhum ângulo selecionado');
}

const calculateCredits = (numAngles) => {
  if (numAngles === 0) return 0;
  if (numAngles <= 2) return 1;
  return 1 + (numAngles - 2);
};

return {
  productId: body.product_id,
  userId: body.user_id,
  imageId: body.image_id,
  angles: angles,
  anglesCount: angles.length,
  creditsNeeded: calculateCredits(angles.length),
  startTime: startTime
};

// ETAPA 1: REMOVER FUNDO
const binary = $input.first().binary;

const apiKey = 'AIzaSyAjxVPQvFgFqFxmI4_MlHB8jeTAxvuIPNM';
const model = 'gemini-2.0-flash-exp';
const endpoint = `https://generativelanguage.googleapis.com/v1beta/models/${model}:generateContent?key=${apiKey}`;

let imageBase64 = null;
let imageMimeType = 'image/png';

if (binary && binary.data) {
  const buffer = await this.helpers.getBinaryDataBuffer(0, 'data');
  imageBase64 = buffer.toString('base64');
  imageMimeType = binary.data.mimeType || 'image/png';
}

if (!imageBase64) throw new Error('Nenhuma imagem encontrada');

const prompt = `Remove the background from this product image completely.
Keep ONLY the product itself.
Make background pure white (#FFFFFF).
Preserve all product details, colors, textures.
Clean edges, no artifacts.
Do NOT modify the product.`;

const payload = {
  contents: [{ role: "user", parts: [
    { inline_data: { mime_type: imageMimeType, data: imageBase64 } },
    { text: prompt }
  ]}],
  generationConfig: { responseModalities: ["TEXT", "IMAGE"], temperature: 0.5 },
  safetySettings: [
    { category: "HARM_CATEGORY_HARASSMENT", threshold: "BLOCK_NONE" },
    { category: "HARM_CATEGORY_HATE_SPEECH", threshold: "BLOCK_NONE" },
    { category: "HARM_CATEGORY_SEXUALLY_EXPLICIT", threshold: "BLOCK_NONE" },
    { category: "HARM_CATEGORY_DANGEROUS_CONTENT", threshold: "BLOCK_NONE" }
  ]
};

const response = await this.helpers.httpRequest({
  method: 'POST', url: endpoint,
  headers: { 'Content-Type': 'application/json' },
  body: payload, json: true, timeout: 180000
});

let cleanImageBase64 = null;
if (response.candidates?.[0]?.content?.parts) {
  for (const part of response.candidates[0].content.parts) {
    if (part.inlineData?.data) cleanImageBase64 = part.inlineData.data;
  }
}

if (!cleanImageBase64) throw new Error('Gemini não retornou imagem limpa');

// IMPORTANTE: Salvar base64 no JSON para passar pelo loop
return [{
  json: {
    cleanImageBase64: cleanImageBase64,
    generationId: $('Criar Geracao').item.json.id,
    product: $('Buscar Produto').item.json
  },
  binary: {
    cleanImage: { data: cleanImageBase64, mimeType: 'image/png', fileName: 'clean.png' }
  }
}];

function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
    const r = Math.random() * 16 | 0;
    return (c == 'x' ? r : (r & 0x3 | 0x8)).toString(16);
  });
}

const input = $input.first();
const userId = $('Preparar Dados').item.json.userId;
const productId = $('Preparar Dados').item.json.productId;
const cleanImageId = generateUUID();

const storagePath = `${userId}/${productId}/clean_base_${cleanImageId}.png`;
const publicUrl = `https://dbdqiqehuapcicejnzyd.supabase.co/storage/v1/object/public/products/${storagePath}`;

return [{
  json: {
    ...input.json,
    cleanImageId,
    cleanStoragePath: storagePath,
    cleanPublicUrl: publicUrl
  },
  binary: input.binary
}];

// Criar um item para cada ângulo, passando o base64 via JSON
const input = $input.first().json;
const angles = $('Preparar Dados').item.json.angles;

const items = angles.map((angle, index) => ({
  json: {
    currentAngle: angle,
    angleIndex: index,
    totalAngles: angles.length,
    cleanImageBase64: input.cleanImageBase64,
    cleanPublicUrl: input.cleanPublicUrl,
    generationId: input.generationId,
    product: input.product
  }
}));

return items;

const input = $input.first().json;
const angle = input.currentAngle;
const product = input.product;

const category = (product.category || 'produto').toLowerCase();
const color = product.color || '';

const prompts = {
  front: `Professional e-commerce photo - FRONT VIEW. Ghost mannequin style. Solid light gray background (#EDEDED).`,
  back: `Professional e-commerce photo - BACK VIEW. Ghost mannequin style. Solid light gray background (#EDEDED).`,
  detail: `Professional DETAIL/CLOSE-UP photo. Focus on texture, stitching. Solid light gray background (#EDEDED).`,
  'side-left': `Professional photo - LEFT SIDE VIEW. Solid light gray background (#EDEDED).`,
  'side-right': `Professional photo - RIGHT SIDE VIEW. Solid light gray background (#EDEDED).`,
  top: `Professional photo - TOP VIEW. Solid light gray background (#EDEDED).`
};

const basePrompt = prompts[angle] || prompts.front;
const fullPrompt = `${basePrompt}\n\nProduct: ${category}, Color: ${color || 'as shown'}\n\nGenerate ${angle.toUpperCase()} view. Preserve exact colors. Professional quality.`;

return [{ json: { ...input, prompt: fullPrompt } }];

// GERAR ÂNGULO - Lê base64 do JSON
const input = $input.first().json;
const prompt = input.prompt;
const angle = input.currentAngle;
const imageBase64 = input.cleanImageBase64;

if (!imageBase64) throw new Error('Imagem limpa não encontrada no JSON');

const apiKey = 'AIzaSyAjxVPQvFgFqFxmI4_MlHB8jeTAxvuIPNM';
const model = 'gemini-2.0-flash-exp';
const endpoint = `https://generativelanguage.googleapis.com/v1beta/models/${model}:generateContent?key=${apiKey}`;

const payload = {
  contents: [{ role: "user", parts: [
    { inline_data: { mime_type: 'image/png', data: imageBase64 } },
    { text: prompt }
  ]}],
  generationConfig: { responseModalities: ["TEXT", "IMAGE"], temperature: 0.8 },
  safetySettings: [
    { category: "HARM_CATEGORY_HARASSMENT", threshold: "BLOCK_NONE" },
    { category: "HARM_CATEGORY_HATE_SPEECH", threshold: "BLOCK_NONE" },
    { category: "HARM_CATEGORY_SEXUALLY_EXPLICIT", threshold: "BLOCK_NONE" },
    { category: "HARM_CATEGORY_DANGEROUS_CONTENT", threshold: "BLOCK_NONE" }
  ]
};

const response = await this.helpers.httpRequest({
  method: 'POST', url: endpoint,
  headers: { 'Content-Type': 'application/json' },
  body: payload, json: true, timeout: 180000
});

let generatedBase64 = null;
if (response.candidates?.[0]?.content?.parts) {
  for (const part of response.candidates[0].content.parts) {
    if (part.inlineData?.data) generatedBase64 = part.inlineData.data;
  }
}

if (!generatedBase64) throw new Error('Gemini não retornou imagem para: ' + angle);

return [{
  json: { ...input, generatedBase64 },
  binary: {
    generatedAngle: { data: generatedBase64, mimeType: 'image/png', fileName: `studio_${angle}.png` }
  }
}];

function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
    const r = Math.random() * 16 | 0;
    return (c == 'x' ? r : (r & 0x3 | 0x8)).toString(16);
  });
}

const input = $input.first();
const angle = input.json.currentAngle;
const userId = $('Preparar Dados').item.json.userId;
const productId = $('Preparar Dados').item.json.productId;
const angleImageId = generateUUID();

const storagePath = `${userId}/${productId}/studio_${angle}_${angleImageId}.png`;
const publicUrl = `https://dbdqiqehuapcicejnzyd.supabase.co/storage/v1/object/public/products/${storagePath}`;

return [{
  json: { ...input.json, angleImageId, angleStoragePath: storagePath, anglePublicUrl: publicUrl },
  binary: input.binary
}];

const items = $input.all();

const generations = items.map(item => ({
  angle: item.json.currentAngle,
  image_url: item.json.anglePublicUrl,
  image_id: item.json.angleImageId
}));

return [{
  json: {
    generationId: items[0].json.generationId,
    cleanImageUrl: items[0].json.cleanPublicUrl,
    generations,
    totalAngles: items.length
  }
}];

